        var e2y = verts[i2 + 1] - verts[i0 + 1];
        var e2z = verts[i2 + 2] - verts[i0 + 2];

        var px = this.dir[1] * e2z - this.dir[2] * e2y;
        var py = this.dir[2] * e2x - this.dir[0] * e2z;
        var pz = this.dir[0] * e2y - this.dir[1] * e2x;

