        // Add features to renderer if layer is attached to planet
        if (this.getGlobe()) {
            this._addFeatureToRenderers(feature);
            if (this.isVisible()) {
                this.getGlobe()
                    .getRenderContext()
                    .requestFrame();
            }
        }
