        var decimal = (absLat - deg) * 60;
        var min = Math.floor(decimal);
        var sec = (decimal - min) * 60;

        return (
            stringSign(degree) +
            this._pad2Digits(deg) +
            String.fromCharCode(176) +
            " " +
            this._pad2Digits(min) +
            "' " +
            this._pad2Digits(Numeric.roundNumber(sec, 2)) +
            '"'
        );
