        if (!dest) {
            dest = new Array(3);
        }

        dest[0] = this.lambda0 + (position3d[0] * 180) / Math.PI;
        dest[1] = (Math.atan(_sinh(position3d[1])) * 180) / Math.PI;
        dest[2] = position3d[2];

